mapper.insert:
/* menu.insertMenu */
INSERT INTO MENU(
	MENU_CD
	, UP_MENU_CD
	, URL
	, MENU_NAME
	, SORT_SEQ
	, USE_YN
	, INS_ID
	, INS_DT
	, UPD_ID
	, UPD_DT
)VALUES(
	#{menuCd}
	, #{upMenuCd}
	, #{url}
	, #{menuName}
	, #{sortSeq}
	, #{useYn}
	, #{insId}
	, #{insDt}
	, #{updId}
	, #{updDt}
)

mapper.update:
/* menu.updateMenu */
UPDATE MENU SET
UP_MENU_CD	= #{upMenuCd}
, URL		= #{url}
, MENU_NAME	= #{menuName}
, USE_YN	= #{useYn}
, SORT_SEQ	= #{sortSeq}
WHERE MENU_CD = #{menuCd}

mapper.delete:
/* menu.deleteMenu */
DELETE FROM MENU
WHERE MENU_CD = #{menuCd}
