mapper.insert:
/* menu.insertMenu */
INSERT INTO PGI_PROJECT(
	PROJECT_CODE
	, PROJECT_NAME
	, PROJECT_COMMENT
	, SORT_SEQ
	, INS_ID
	, INS_DT
	, UPD_ID
	, UPD_DT
)VALUES(
	#{projectCode}
	, #{projectName}
	, #{projectComment}
	, #{sortSeq}
	, #{insId}
	, #{insDt}
	, #{updId}
	, #{updDt}
)

mapper.update:
/* menu.updateMenu */
UPDATE PGI_PROJECT SET
	PROJECT_NAME = #{projectName}
	, PROJECT_COMMENT = #{projectComment}
	, SORT_SEQ	= #{sortSeq}
WHERE PROJECT_CODE = #{projectCode}

mapper.delete:
/* menu.deleteMenu */
DELETE FROM PGI_PROJECT
WHERE PROJECT_CODE = #{projectCode}
